import { Component } from '@angular/core';
import { DocumentoService } from '../services/documento.service';

@Component({
  selector: 'app-subir-archivo',
  templateUrl: './subir-archivo.component.html',
  styleUrls: ['./subir-archivo.component.css']
})
export class SubirArchivoComponent {
  // Propiedades para el formulario
  titulo: string = '';
  autores: string = '';
  categoria: string = '';
  tipoDocumento: string = '';
  palabrasClave: string = '';
  fechaPublicacion: string = '';
  formato: any;

  constructor(private documentoService: DocumentoService) {}

  // Método para manejar el envío del formulario
    subirArchivo(): void {
    if (!this.selectedFile) {
      console.error('No se seleccionó ningún archivo.');
      return;
    }
  
    const formData = new FormData();
  
    // Agregar campos al FormData
    formData.append('Titulo', this.titulo);
    formData.append('Autores', this.autores);
    formData.append('Categoria', this.categoria);
    formData.append('TipoDocumento', this.tipoDocumento);
    formData.append('PalabrasClave', this.palabrasClave);
    formData.append('FechaPublicacion', this.fechaPublicacion);
    formData.append('Archivo', this.selectedFile, this.selectedFile.name);
  
    this.documentoService.subirDocumento(formData).subscribe({
      next: (response: any) => console.log('Documento y archivo subidos exitosamente:', response),
      error: (err: any) => console.error('Error al subir el documento:', err),
    });
  }
  
  

  selectedFile: File | null = null;

  onFileSelected(event: any): void {
      const file: File = event.target.files[0];
      if (file) {
          this.selectedFile = file;
          console.log('Archivo seleccionado:', file.name);
      }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Repositorio UCB</title>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css">
    <link rel="stylesheet" href="subir-archivo.component.css">     
</head>
<body>
    <header>
        <nav class="navbar navbar-expand-lg">
            <div class="container">
                <a class="navbar-brand" href="#">Repositorio UCB</a>
                <a href="perfil.component.html">
                    <button class="btn btn-login">Ver Perfil</button>
                </a>
            </div>
        </nav>
    </header>
    <div class="header2">
        <nav class="navbar navbar-expand-lg">
            <div class="container">
                <div class="nav2">
                    <a href="index.html">
                        <button class="btn-outline-dark custom-btn">Repositorio principal</button>
                    </a>
                    &rarr;
                    <button class="btn-outline-dark custom-btn">Login</button>
                </div>
            </div>
        </nav>
    </div>

    <main class="container my-4">
        <h1 class="text-center mb-4">Subir Archivo</h1>
        <form (submit)="subirArchivo()">
            <div class="mb-3">
                <label for="titulo" class="form-label">Título</label>
                <input type="text" [(ngModel)]="titulo" id="titulo" name="titulo" class="form-control" placeholder="Título del documento" required>
            </div>
            <div class="mb-3">
                <label for="autores" class="form-label">Autores</label>
                <input type="text" [(ngModel)]="autores" id="autores" name="autores" class="form-control" placeholder="Apellidos, Nombres" required>
            </div>
            <div class="mb-3">
                <label for="categoria" class="form-label">Categoría</label>
                <input type="text" [(ngModel)]="categoria" id="categoria" name="categoria" class="form-control" placeholder="Ejemplo: Ingeniería" required>
            </div>
            <div class="mb-3">
                <label for="tipoDocumento" class="form-label">Tipo de Documento</label>
                <select [(ngModel)]="tipoDocumento" id="tipoDocumento" name="tipoDocumento" class="form-select" required>
                    <option value="" disabled selected>Seleccione un tipo</option>
                    <option value="Tesis">Tesis</option>
                    <option value="Artículo">Artículo</option>
                    <option value="Proyecto">Proyecto</option>
                </select>
            </div>
            <div class="mb-3">
                <label for="palabrasClave" class="form-label">Palabras Clave</label>
                <input type="text" [(ngModel)]="palabrasClave" id="palabrasClave" name="palabrasClave" class="form-control" placeholder="Palabras clave separadas por coma" required>
            </div>
            <div class="mb-3">
                <label for="fechaPublicacion" class="form-label">Fecha de Publicación</label>
                <input type="date" [(ngModel)]="fechaPublicacion" id="fechaPublicacion" name="fechaPublicacion" class="form-control" required>
            </div>
            <!-- Archivo adjunto -->
            <div class="mb-3">
                <label for="archivoAdjunto" class="form-label">Archivo adjunto</label>
                <input
                    type="file"
                    class="form-control"
                    id="archivoAdjunto"
                    (change)="onFileSelected($event)"
                    accept=".pdf"
                />
                <div class="form-text">
                    El archivo debe ser nombrado con su apellido. Ej: "moreno.pdf". El archivo debe ser .pdf
                </div>
            </div>
            <button type="submit" class="botones-Sub">Subir Documento</button>
        </form>
    </main>

  <footer>
    <div class="footer-content text-center">
        <p>&copy; 2024 Universidad Católica Boliviana. Todos los derechos reservados.</p>
        <div class="footer-links">
            <ul>
                <li><a href="#">Casos de Uso</a></li>
                <li><a href="#">Explorar</a></li>
                <li><a href="#">Recursos</a></li>
                <li><a href="Soporte.html">Soporte</a></li>
            </ul>
        </div>
    </div>
</footer>
</body>
</html>
